mapper.select:
select b.file_bus_id, b.bus_uuid, a.file_uuid, a.file_name, a.file_length, a.file_status, a.file_addtime, a.file_path, a.file_type, (select aaa103 from v_aa10 t where t.aaa100='FILE_BUS_TYPE' and aaa102=b.file_bus_type) file_bus_type from s_file_record a, s_bus_file_record b where a.file_uuid=b.file_uuid and b.file_bus_id=#{file_bus_id}
and b.file_bus_type =#{file_bus_type}
order by file_addtime desc
